import React, { Component } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const API_KEY= 'http://api.openweathermap.org/data/2.5/forecast?id=524901&APPID=ef457c2a6e81b0def86b58c94370bd4f';

// const DEFAULT_QUERY = 'redux';


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
        items: {},
        IsLoaded:false
    };
}


componentDidMount = () => {
  navigator.geolocation.getCurrentPosition(position => {
    const { latitude, longitude } = position.coords;
    this.getWeather(latitude, longitude);
  });
};


getWeather = (latitude, longitude) => {
  const API_KEY = "3de6162d3745365b168ade2bbe4e1d6";
  const api = `http://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=${API_KEY}`;
  fetch(api)
    .then(response => {
      if (response.ok) {
        return response
      }
      else { 
        let error = new Error(`Error ${response.status} is ${response.statusText}`);
        throw error;
      }
     })
    .then(response => response.json())
    .then(data => {
      this.setState({
        locationName: data.name,
        temperature: data.main.temp,
        weatherDescription: data.weather[0].description,
        isLoading: false,
      });
    })
    .catch(error => { 
      alert(`Data could not be fetched ${error.message}`)
    });
};

  render() {

    const { locationName, temperature, weatherDescription } = this.state;

    return (
      <div className="container-fluid text-white my-auto">
      {/* <div className="container mx-auto my-4 py-4">
        <div className="row justify-content-center text-center">
          <h1 className="col-12 display-4 my-2 py-3 text-success">
            Awesome Weather App
          </h1>      */}

              <h3 className="col-12 text-danger">{locationName}</h3>
              <h3 className="col-12 text-danger">{temperature}</h3>
              <h3 className="col-12 text-danger">{weatherDescription}</h3>
        </div>
    )
    //   </div>
    // </div>

            }
}


export default App;
