import React, { Component } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const API = 'http://api.openweathermap.org/data/2.5/forecast?id=524901&APPID=ef457c2a6e81b0def86b58c94370bd4f';
const DEFAULT_QUERY = '';


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
        hits: [],
    };
}

componentDidMount(){
  navigator.geolocation.getCurrentPosition(function(position) {
    do_something(position.coords.latitude, position.coords.longitude);

    fetch(API)
    .then(response => response.json())
    .then(data => this.setState({ hits: data.hits }));
  });
}



  render() {
    const { hits } = this.state;

    return (
      <div className="container-fluid text-white my-auto">
      <div className="container mx-auto my-4 py-4">
        <div className="row justify-content-center text-center">
          <h1 className="col-12 display-4 my-2 py-3 text-success">
            Awesome Weather App
          </h1>     

              <h2 className="col-12">Location Name</h2>
              <h3 className="col-12 text-danger">{hits.cod} </h3>
              <h3 className="col-12">Weather Description</h3>
        </div>
      </div>
    </div>
    );
  }

}
export default App;
