import React, { Component } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Weatherpic from './Weatherpic';
import Button from './Button';


class App extends Component {
  constructor() {
    super();
    this.state = {
        items: {},
        IsLoaded:true,

    };
}


componentDidMount = () => {
  navigator.geolocation.getCurrentPosition(position => {
    const { latitude, longitude } = position.coords;
    this.getWeather(latitude, longitude);
  });
};


getWeather = (latitude, longitude) => {
  const API_KEY = "3de6162d3745365b168ade2bbe4e1d66";
  const api = `http://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=${API_KEY}`;
  fetch(api)
    .then(response => {
      if (response.ok) {
        return response
      }
      else { 
        let error = new Error(`Error ${response.status} is ${response.statusText}`);
        throw error;
      }
     })
    .then(response => response.json())
    .then(data => {
      this.setState({
        locationName: data.name,
        temperature: data.main.temp,
        weatherDescription: data.weather[0].description,
        IsLoaded: false,
        celsuisToFara: false
      });
    })
    .catch(error => { 
      alert(`Data could not be fetched ${error.message}`)
    });
};

changeToCelsuis = (temperature) => {

  let celsuis= (temperature - 32) * 5/9;

  toggleCandF(celsuis, temperature)
}

toggleCandF= (celsuis,temperature) => {
  this.celsuisToFara =!this.celsuisToFara;

  if(this.celsuisToFara){
  this.setState({
      temperature: celsuis
  });
}
    else {
     this.setState({
       temperature: temperature
     })
}
}

  render() {

    const { locationName, temperature, weatherDescription, IsLoaded} = this.state;

    return (
      IsLoaded ? (<h1>Loading....</h1>):
        (<div className="d-flex flex-column align-items-center">
        <h1 className="text-success">Weather App</h1>
              <h3 className="col-12 text-danger">{locationName}</h3>
              <h3 className="col-12 text-danger">{temperature}</h3>

              <h3 className="col-12 text-danger">{weatherDescription}</h3>
              <Weatherpic temperature= {temperature}/>
              <Button title= "convert" onClick={() => this.changeToCelsuis(temperature)} />
              </div> )
    )
    }
  }


export default App;
